mapper.select:
SELECT id, member_no, diary_date, content, image_path
FROM diary
WHERE member_no = #{memberNo}
  AND diary_date BETWEEN #{startDate} AND #{endDate}

SELECT id, member_no, diary_date, content, image_path
FROM diary
WHERE member_no = #{memberNo} AND diary_date = #{diaryDate}

mapper.update:
UPDATE diary
SET content = #{content},
    image_path = #{imagePath}
WHERE id = #{id}
