mapper.select:
SELECT id, member_no, parent_id, content, completed, created_at, due_date
FROM todo
WHERE member_no = #{memberNo}
ORDER BY IFNULL(parent_id, id), parent_id, created_at ASC;

SELECT id, member_no, parent_id, content, completed, created_at, due_date
FROM todo
WHERE id = #{id};

mapper.update:
UPDATE todo
SET content = #{content},
    completed = #{completed},
    due_date = #{dueDate}
WHERE id = #{id};
